window.addEventListener('scroll', function() {
  const images = document.querySelectorAll('.scroll-zoom, .scroll-color');

  images.forEach(image => {
    const imageTop = image.getBoundingClientRect().top;
    const imageBottom = image.getBoundingClientRect().bottom;
    const windowHeight = window.innerHeight;

    if (imageTop < windowHeight && imageBottom >= 0) {
      image.classList.add('animate-scroll-zoom');
      image.classList.add('animate-scroll-color');
    } else {
      image.classList.remove('animate-scroll-zoom');
      image.classList.remove('animate-scroll-color');
    }
  });
});

