function copyNodeAsImageToClipboard(node) {
  // Step 1: Render the HTML node on a canvas element
  const canvas = document.createElement('canvas');
  const context = canvas.getContext('2d');
  const width = node.offsetWidth;
  const height = node.offsetHeight;

  canvas.width = width;
  canvas.height = height;

  const data = new XMLSerializer().serializeToString(node);
  const DOMURL = window.URL || window.webkitURL || window;

  const img = new Image();
  const svgBlob = new Blob([data], { type: 'image/svg+xml;charset=utf-8' });
  const url = DOMURL.createObjectURL(svgBlob);

  img.onload = function() {
    // Step 2: Convert the canvas content to an image data URL
    context.drawImage(img, 0, 0);

    const imageDataURL = canvas.toDataURL('image/png');

    // Step 3: Create a temporary element to hold the image
    const tempElem = document.createElement('div');
    tempElem.contentEditable = true;
    const imgElem = document.createElement('img');
    imgElem.src = imageDataURL;
    tempElem.appendChild(imgElem);

    document.body.appendChild(tempElem);

    // Step 4: Copy the image to the clipboard
    const range = document.createRange();
    range.selectNode(tempElem);
    window.getSelection().removeAllRanges();
    window.getSelection().addRange(range);
    document.execCommand('copy');

    // Step 5: Clean up any temporary elements created
    document.body.removeChild(tempElem);
    DOMURL.revokeObjectURL(url);
  };

  img.src = url;
}

// Usage: Pass the HTML node you want to copy as an image to the clipboard
const nodeToCopy = document.getElementById('your-node-id');
copyNodeAsImageToClipboard(nodeToCopy);

