export const formatCollapsingText = (text, shouldCollapse, isCollapsed, minLength) => {
  if (shouldCollapse && isCollapsed) {
    const indexOfLastSpace = text.lastIndexOf(' ', minLength);
    return `${text.substring(0, indexOfLastSpace).trim()}...`;
  }

  return text;
};

