import NextAuth from 'next-auth';
import Providers from 'next-auth/providers';
import { MongoClient } from 'mongodb';

const mongoClient = new MongoClient(process.env.MONGODB_URI, {
  useNewUrlParser: true,
  useUnifiedTopology: true,
});

export default NextAuth({
  // Configure one or more authentication providers
  providers: [
    Providers.Credentials({
      // The name to display on the sign-in form (e.g., 'Sign in with...')
      name: 'Credentials',
      // The credentials authentication provider requires a valid MongoDB connection
      credentials: {
        async authorize(credentials) {
          try {
            // Connect to MongoDB
            await mongoClient.connect();

            // Access your MongoDB collection
            const users = mongoClient.db().collection('users');

            // Find user by email and password
            const user = await users.findOne({
              email: credentials.email,
              password: credentials.password,
            });

            // If user is found, return the user object
            if (user) {
              return Promise.resolve(user);
            } else {
              // If user is not found, reject the authentication
              return Promise.reject(new Error('Invalid email or password'));
            }
          } catch (error) {
            // Handle any errors
            console.error(error);
            return Promise.reject(new Error('Internal server error'));
          } finally {
            // Close the MongoDB connection
            await mongoClient.close();
          }
        },
      },
    }),
    // Add other providers here (e.g., Google, GitHub, etc.)
  ],
});

