const fs = require('fs');

// 1. Extract the version information from the commit message
const commitMessage = 'chore: Bump version to 1.2.3';
const versionRegex = /Bump version to (\d+\.\d+\.\d+)/;
const versionMatch = commitMessage.match(versionRegex);
const version = versionMatch ? versionMatch[1] : null;

if (version) {
  // 2. Read the package.json file
  const packageJsonPath = './package.json';
  const packageJson = JSON.parse(fs.readFileSync(packageJsonPath, 'utf8'));

  // 3. Update the version field
  packageJson.version = version;

  // 4. Write the updated package.json file
  fs.writeFileSync(packageJsonPath, JSON.stringify(packageJson, null, 2));

  console.log('Version updated in package.json:', version);
} else {
  console.log('Version not found in commit message.');
}
