import { prisma } from '~/utils/db.server';
import { totp } from 'notp';
import crypto from 'crypto';

type VerificationType = 'email' | 'passwordReset' | '2FA'; // Add more verification types as required

interface CreateTOTPOptions {
  verificationTarget: string;
  type: VerificationType;
  expirationTime: Date;
}

interface VerifyTOTPOptions {
  verificationTarget: string;
  type: VerificationType;
  otp: string;
}

export async function createTOTP({ verificationTarget, type, expirationTime }: CreateTOTPOptions) {
  const secretKey = generateSecretKey();

  // Delete any existing verification for this target and type
  await prisma.verification.deleteMany({
    where: {
      verificationTarget,
      type,
    },
  });

  const otp = totp.gen(secretKey);

  // Create a new verification record
  const newVerification = await prisma.verification.create({
    data: {
      verificationTarget,
      type,
      secretKey,
      otp,
      expirationTime,
    },
  });

  return otp;
}

export async function verifyTOTP({ verificationTarget, type, otp }: VerifyTOTPOptions) {
  // Get the verification record
  const verification = await prisma.verification.findUnique({
    where: {
      verificationTarget_type: {
        verificationTarget,
        type,
      },
    },
  });

  if (!verification) {
    throw new Error('Verification record not found');
  }

  // Check if OTP is valid
  const isOTPValid = totp.verify(otp, verification.secretKey);

  if (!isOTPValid) {
    throw new Error('Invalid OTP');
  }

  // Check if OTP is expired
  const currentTime = new Date();
  if (currentTime > verification.expirationTime) {
    throw new Error('Expired OTP');
  }

  // If OTP is valid and not expired, delete the verification record
  await prisma.verification.delete({
    where: {
      verificationTarget_type: {
        verificationTarget,
        type,
      },
    },
  });

  return true;
}

function generateSecretKey() {
  return crypto.randomBytes(32).toString('hex');
}

import { prisma } from '~/utils/db.server'
import * as otpLib from 'notp'
import { URLSearchParams } from 'url'

interface VerificationArgs {
  email: string
  type: string
  expirationTime: number
}

export async function createVerification({ email, type, expirationTime }: VerificationArgs): Promise<string> {
  const otp = otpLib.totp.gen(email, { time: expirationTime })

  await prisma.verification.upsert({
    where: { verificationTarget_type: { verificationTarget: email, type } },
    update: { otp, expirationTime: Math.floor(Date.now() / 1000) + expirationTime },
    create: { verificationTarget: email, type, otp, expirationTime: Math.floor(Date.now() / 1000) + expirationTime }
  })

  const params = new URLSearchParams({ email, otp })
  return `https://example.com/verify?${params.toString()}`
}

