    where: { verificationTarget_type: { verificationTarget: email, type: 'email_verification' } },
  })

  if (!verification || Date.now() / 1000 > verification.expirationTime) return false

  const isValid = otpLib.totp.verify(otp, email, { time: verification.expirationTime })

  if (isValid) {
    // Handle post-verification process here.
  }

  return isValid
}
