async function getDownloadUrlByFileName(fileName: string): Promise<string | undefined> {
  const response = await gapi.client.drive.files.list({
    pageSize: 10,
    fields: 'nextPageToken, files(id, name)',
    q: `name='${fileName}'`
  });

  const file = response.result.files.find(file => file.name === fileName);

  if (file) {
    return `https://www.googleapis.com/drive/v3/files/${file.id}?alt=media`;
  } else {
    return undefined;
  }
}

async listFiles(folderId: string, pageToken?: string): Promise<GoogleFileListResponse> {
  const response = await gapi.client.drive.files.list({
    pageSize: 10,
    fields: 'nextPageToken, files(id, name, createdTime)',
    q: `'${folderId}' in parents and mimeType='application/json'`,
    pageToken: pageToken,
    orderBy: 'createdTime desc',
  });

  return response.result as GoogleFileListResponse;
}

async function getDownloadUrlByFileName(folderId: string, fileName: string): Promise<string | undefined> {
  const response = await gapi.client.drive.files.list({
    pageSize: 10,
    fields: 'nextPageToken, files(id, name)',
    q: `'${folderId}' in parents and name='${fileName}'`
  });

  const file = response.result.files.find(file => file.name === fileName);

  if (file) {
    return `https://www.googleapis.com/drive/v3/files/${file.id}?alt=media`;
  } else {
    return undefined;
  }
}

async createFolder(name: string): Promise<GoogleFile> {
  const fileMetadata = {
    'name': name,
    'mimeType': 'application/vnd.google-apps.folder'
  };

  const response = await gapi.client.drive.files.create({
    resource: fileMetadata,
    fields: 'id'
  });

  return response.result as GoogleFile;
}

async createFolder(name: string): Promise<string> {
  const fileMetadata = {
    'name': name,
    'mimeType': 'application/vnd.google-apps.folder'
  };

  const response = await gapi.client.drive.files.create({
    resource: fileMetadata,
    fields: 'id'
  });

  return response.result.id;
}

async createFolderIdFile(folderId: string): Promise<void> {
  const fileMetadata = {
    'name' : 'folderID.json',
    'mimeType' : 'application/json'
  };

  const fileContent = new Blob([JSON.stringify({folderId})], {type: 'application/json'});

  const form = new FormData();
  form.append('metadata', new Blob([JSON.stringify(fileMetadata)], {type: 'application/json'}));
  form.append('file', fileContent);

  await gapi.client.request({
    path: '/upload/drive/v3/files',
    method: 'POST',
    params: {uploadType: 'multipart'},
    body: form,
  });
}

async getFolderIdFromFile(): Promise<string> {
  const response = await gapi.client.drive.files.list({
    pageSize: 1,
    fields: 'files(id, name)',
    q: `name='folderID.json'`
  });

  const file = response.result.files.find(file => file.name === 'folderID.json');

  if (file) {
    const fileResponse = await gapi.client.drive.files.get({
      fileId: file.id,
      alt: 'media'
    });

    return JSON.parse(fileResponse.body).folderId;
  } else {
    throw new Error('folderID.json file not found');
  }
}

