import { useState, useLayoutEffect } from 'react';

const ComponentWithMeasurements = () => {
  const [elementWidth, setElementWidth] = useState(null);

  // This useLayoutEffect runs after React has updated the virtual DOM
  // but before the browser visually updates the page.
  useLayoutEffect(() => {
    // Perform measurements or read layout information
    const element = document.getElementById('some-element-id');
    const width = element ? element.offsetWidth : null;

    // Save the measurements in state
    setElementWidth(width);
  }, []); // Empty dependency array to ensure it runs only once after mount

  return (
    <div>
      <div id="some-element-id">Element with measurements</div>
      <p>Element width: {elementWidth}</p>
    </div>
  );
};

