  const handleInputChange = async (event) => {
    const query = event.target.value;
    setQuery(query);

    if (query.length > 0) {
      try {
        const searchResults = await index.search(query);
        setResults(searchResults.hits);
      } catch (error) {
        console.error('Error retrieving search results', error);
      }
    } else {
      setResults([]);
    }
  };

  return (
    <div>
      <input type="text" value={query} onChange={handleInputChange} />
      {results.map((result) => (
        <div key={result.objectID}>{result.title}</div>
      ))}
    </div>
  );
}

#include <gdal_priv.h>
#include <ogr_spatialref.h>
#include <proj.h>
#include <thread>
#include <mutex>

std::mutex projMutex; // Mutex for protecting Proj context creation

void workerFunction()
{
    // Create a new Proj context for each thread
    proj_context* projCtx;
    {
        std::lock_guard<std::mutex> lock(projMutex);
        projCtx = proj_context_create();
    }

    // Set the Proj context on the GDAL/OGR library
    CPLSetThreadLocalConfigOption("OGR_ENABLE_PARTIAL_REPROJECTION", "YES");
    CPLSetThreadLocalConfigOption("OGR_ENABLE_PARTIAL_REPROJECTION_THREADS", "YES");
    CPLSetThreadLocalConfigOption("PROJ_THREAD_SAFE", "YES");
    OGRRegisterAll();

    // Create separate source and target spatial reference systems
    OGRSpatialReference sourceSRS;
    OGRSpatialReference targetSRS;
    sourceSRS.importFromEPSG(4326);
    targetSRS.importFromEPSG(3857);

    // Create a new OGRCoordinateTransformation object
    OGRCoordinateTransformation* transformation = OGRCreateCoordinateTransformation(&sourceSRS, &targetSRS);

    // Use the transformation object safely

    // Clean up
    OGRCoordinateTransformation::DestroyCT(transformation);
    {
        std::lock_guard<std::mutex> lock(projMutex);
        proj_context_destroy(projCtx);
    }
}

int main()
{
    // Create multiple worker threads
    int numThreads = 4;
    std::vector<std::thread> threads;
    for (int i = 0; i < numThreads; ++i)
    {
        threads.emplace_back(workerFunction);
    }

    // Wait for all threads to complete
    for (std::thread& thread : threads)
    {
        thread.join();
    }

    return 0;
}

