pcre* compileRegex(const char* pattern) {
    const char* error;
    int errorOffset;
    pcre* re = pcre_compile(pattern, 0, &error, &errorOffset, NULL);
    if (re == nullptr) {
        std::cerr << "Failed to compile regex: " << error << std::endl;
    }
    return re;
}

bool matchRegex(pcre* re, const char* subject) {
    int subjectLength = strlen(subject);
    int ovector[30];  // This array will store captured substring positions
    int rc = pcre_exec(re, NULL, subject, subjectLength, 0, 0, ovector, 30);
    if (rc >= 0) {
        std::cout << "Match found!" << std::endl;
        return true;
    } else if (rc == PCRE_ERROR_NOMATCH) {
        std::cout << "No match found." << std::endl;
    } else {
        std::cerr << "Regex matching error: " << rc << std::endl;
    }
    return false;
}

int main() {
    const char* regexPattern = "hello\\s+world";
    const char* subject = "hello   world";

    pcre* compiledRegex = compileRegex(regexPattern);
    if (compiledRegex != nullptr) {
        matchRegex(compiledRegex, subject);
        pcre_free(compiledRegex);  // Free the compiled pattern after you're done with it
    }

    return 0;
}

