  let calcString = '';

  $('.calculator-keys button').on('click', function() {
    inputValue = $(this).val();

    if ($(this).hasClass('operator')) {
      operator = inputValue;
      calcString += operator;
    } else if ($(this).hasClass('all-clear')) {
      calcScreen.val('');
      calcString = '';
    } else if ($(this).hasClass('equal-sign')) {
      try {
        calcScreen.val(eval(calcString));
        calcString = '';
      } catch (e) {
