      calcScreen.val(calcScreen.val() + inputValue);
    }
  });
});

$(document).ready(function() {
    let calcScreen = $('#calcScreen');
    let operator = '';
    let currentNumber = '';
    let stack = [];

    const operators = {
        '+': (a, b) => a + b,
        '-': (a, b) => a - b,
        '*': (a, b) => a * b,
        '/': (a, b) => a / b
    };

    $('.calculator-keys button').on('click', function() {
        let buttonValue = $(this).val();

        if ($(this).hasClass('operator')) {
            if (currentNumber !== '') {
                stack.push(parseFloat(currentNumber));
                currentNumber = '';
            }
            operator = buttonValue;
            stack.push(operator);
        } else if ($(this).hasClass('all-clear')) {
            calcScreen.val('');
            currentNumber = '';
            stack = [];
        } else if ($(this).hasClass('equal-sign')) {
            if (currentNumber !== '') {
                stack.push(parseFloat(currentNumber));
                currentNumber = '';
            }
            while (stack.length > 1) {
                let rightOperand = stack.pop();
                let operator = stack.pop();
                let leftOperand = stack.pop();

                if (operator === '/' && rightOperand === 0) {
                    calcScreen.val('Error: Divide by Zero');
                    currentNumber = '';
                    stack = [];
                    return;
                }

                let result = operators[operator](leftOperand, rightOperand);
                stack.push(result);
            }
            calcScreen.val(stack[0]);
            currentNumber = '';
        } else {
            currentNumber += buttonValue;
            calcScreen.val(calcScreen.val() + buttonValue);
        }
    });
});

