class Lesson {
  constructor(id, title, maxCapacity, enrolledStudents = []) {
    this.id = id;
    this.title = title;
    this.maxCapacity = maxCapacity;
    this.enrolledStudents = enrolledStudents;
  }

  // Business logic for enrolling a student in the lesson
  enrollStudent(studentId) {
    if (this.enrolledStudents.includes(studentId)) {
      throw new Error('Student is already enrolled in this lesson.');
    }

    if (this.enrolledStudents.length >= this.maxCapacity) {
      throw new Error('Lesson is already at maximum capacity.');
    }

    this.enrolledStudents.push(studentId);
  }

  // Business logic for unenrolling a student from the lesson
  unenrollStudent(studentId) {
    const index = this.enrolledStudents.indexOf(studentId);
    if (index === -1) {
      throw new Error('Student is not enrolled in this lesson.');
    }

    this.enrolledStudents.splice(index, 1);
  }

  // Business logic to check if the lesson is full
  isFull() {
    return this.enrolledStudents.length >= this.maxCapacity;
  }

  // Business logic to get the available seats in the lesson
  availableSeats() {
    return this.maxCapacity - this.enrolledStudents.length;
  }
}

