const Slideshow = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const elements = [
    { id: 1, content: 'Element 1' },
    { id: 2, content: 'Element 2' },
    { id: 3, content: 'Element 3' },
  ];

  const slideLeft = () => setCurrentIndex((prevIndex) => (prevIndex === 0 ? elements.length - 1 : prevIndex - 1));
  const slideRight = () => setCurrentIndex((prevIndex) => (prevIndex === elements.length - 1 ? 0 : prevIndex + 1));

  return (
    <div className="slideshow">
      <AnimatePresence initial={false} custom={currentIndex}>
        <motion.div
          key={currentIndex}
          custom={currentIndex}
          initial={{ opacity: 0, x: 100 }}
          animate={{ opacity: 1, x: 0 }}
          exit={{ opacity: 0, x: -100 }}
          transition={{ duration: 0.5 }}
          className="slideshow-element"
        >
          {elements[currentIndex].content}
        </motion.div>
      </AnimatePresence>
      <button onClick={slideLeft}>Previous</button>
      <button onClick={slideRight}>Next</button>
    </div>
  );
};

export default Slideshow;

