HWND GetHWNDFromQMdiSubWindow(QMdiSubWindow* subWindow)
{
    if (subWindow) {
        QWidget* widget = subWindow->widget();
        if (widget) {
            WId wid = widget->winId();
            if (wid != 0) {
                HWND hWnd = reinterpret_cast<HWND>(wid);
                return hWnd;
            }
        }
    }

    return nullptr;
}

#include <windows.h>

int GetMonitorScaleFactor(HWND hWnd)
{
    HWND hTopWnd = GetAncestor(hWnd, GA_ROOT);
    HMONITOR hMonitor = MonitorFromWindow(hTopWnd, MONITOR_DEFAULTTONEAREST);

    UINT dpiX, dpiY;
    if (GetDpiForMonitor(hMonitor, MDT_EFFECTIVE_DPI, &dpiX, &dpiY) == S_OK) {
        if (dpiX == 96)
            return 100;  // 100% scaling
        else if (dpiX == 120)
            return 125;  // 125% scaling
        else if (dpiX == 144)
            return 150;  // 150% scaling
        else if (dpiX == 192)
            return 200;  // 200% scaling
        // Add more conditions for other scaling factors if needed
    }

    return 0;  // Unknown or unsupported scaling factor
}

