CREATE TABLE applied_migrations (
    identifier TEXT NOT NULL PRIMARY KEY,
    ddl TEXT NOT NULL,
    applied_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT NOW(),
    requires TEXT[] DEFAULT NULL
);

CREATE FUNCTION apply_migration_with_deps (migration_name TEXT, ddl TEXT, dependencies TEXT[] DEFAULT NULL) RETURNS BOOLEAN
    AS $$
DECLARE
    missing_dependency TEXT;
BEGIN
    IF NOT EXISTS (SELECT FROM applied_migrations WHERE identifier = migration_name) THEN
        IF array_length(dependencies, 1) IS NOT NULL THEN
            FOREACH missing_dependency IN ARRAY dependencies
            LOOP
                IF NOT EXISTS (SELECT FROM applied_migrations WHERE identifier = missing_dependency) THEN
                    RAISE EXCEPTION 'Missing dependency: %', missing_dependency;
                END IF;
            END LOOP;
        END IF;

        EXECUTE ddl;
        INSERT INTO applied_migrations (identifier, ddl, requires) VALUES (migration_name, ddl, dependencies);
        RETURN TRUE;
    END IF;
    RETURN FALSE;
END;
$$ LANGUAGE plpgsql;

SELECT apply_migration_with_deps('create_things_table',
$$
  CREATE TABLE things (
    name TEXT
  );
$$);

SELECT apply_migration_with_deps('alter_things_table',
$$
  ALTER TABLE things ADD number INTEGER;
  ALTER TABLE things ALTER name SET NOT NULL;
$$,
ARRAY['create_things_table']);

SELECT apply_migration_with_deps('create_things_table',
$$
  CREATE TABLE things (
    name TEXT
  );
$$,
NULL,  -- No dependencies
$$
  DROP TABLE things;
$$);  -- Rollback script

CREATE FUNCTION rollback_migration (migration_name TEXT) RETURNS BOOLEAN
    AS $$
DECLARE
    dependent_migration TEXT;
    rollback_ddl TEXT;
BEGIN
    SELECT rollback_ddl INTO rollback_ddl FROM applied_migrations WHERE identifier = migration_name;
    IF NOT FOUND THEN
        RAISE EXCEPTION 'Migration % not found', migration_name;
    END IF;

    -- Check for dependent migrations
    SELECT identifier INTO dependent_migration FROM applied_migrations WHERE requires @> ARRAY[migration_name];
    IF FOUND THEN
        RAISE EXCEPTION 'Cannot roll back migration % because it is required by migration %', migration_name, dependent_migration;
    END IF;

    EXECUTE rollback_ddl;
    DELETE FROM applied_migrations WHERE identifier = migration_name;
    RETURN TRUE;
END;
$$ LANGUAGE plpgsql;

