async function startRecording() {
    recording = true;
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    audioRecorder = new MediaRecorder(stream);
    let audioChunks = [];

    audioRecorder.addEventListener('dataavailable', event => {
        audioChunks.push(event.data);
    });

    audioRecorder.addEventListener('stop', () => {
        audioBlob = new Blob(audioChunks);
        audioChunks = [];
        transcribeAudio();
    });

    audioRecorder.start();
    document.getElementById('recordButton').textContent = 'Stop Recording';
}

function stopRecording() {
    audioRecorder.stop();
    document.getElementById('recordButton').textContent = 'Start Recording';
    recording = false;
}

async function transcribeAudio() {
    const formData = new FormData();
    formData.append('audio', audioBlob);

    // Replace with your endpoint.
    const whisperResponse = await fetch('/whisper-endpoint', {
        method: 'POST',
        body: formData,
    });

    const whisperData = await whisperResponse.json();
    const transcription = whisperData.transcript;
    sendToGPT(transcription);
}

async function sendToGPT(transcription) {
    // Replace with your endpoint and API Key.
    const gptResponse = await fetch('/gpt-endpoint', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer ' + process.env.OPENAI_KEY
        },
        body: JSON.stringify({
            'prompt': `Please clean up and summarize the following transcription: ${transcription}`,
            'max_tokens': 200
        }),
    });

    const gptData = await gptResponse.json();
    document.getElementById('transcription').textContent = gptData.choices[0].text;
}

const axios = require('axios');
const multer = require('multer');
const express = require('express');
require('dotenv').config();

const router = express.Router();

const upload = multer();

router.post('/', upload.single('audio'), async (req, res) => {
    const audioData = req.file.buffer;

    try {
        const response = await axios({
            method: 'post',
            url: 'https://api.openai.com/v1/engines/whisper/mimic',
            headers: {
                'Content-Type': 'audio/raw;sample_rate=16000;encoding=linear16',
                'Authorization': `Bearer ${process.env.OPENAI_KEY}`
            },
            data: audioData
        });

        res.json(response.data);
    } catch (err) {
        console.error(err);
        res.status(500).json({ message: 'Server error' });
    }
});

module.exports = router;

