{
  "manifest_version": 2,
  "name": "Github PR Fetch",
  "version": "1.0",
  "description": "Display git fetch URL and ref name for a Github pull request",
  "permissions": ["activeTab", "https://github.com/*"],
  "content_scripts": [
    {
      "matches": ["https://github.com/*/*/*/*/pull/*/"],
      "js": ["content.js"]
    }
  ]
}

function insertGitFetchCommand(user, repo, prNum) {
  // Create the div that contains the fetch command.
  var fetchDiv = document.createElement("div");
  var fetchCommand = `git fetch https://github.com/${user}/${repo}.git +refs/pull/${prNum}/head`;

  fetchDiv.innerHTML = `<pre>${fetchCommand}</pre>`;

  // Create the copy button.
  var copyButton = document.createElement("button");
  copyButton.innerText = "Copy";
  copyButton.addEventListener("click", function() {
    navigator.clipboard.writeText(fetchCommand);
  });

  // Add the button to the div.
  fetchDiv.appendChild(copyButton);

  // Add the div to the page.
  var header = document.getElementById("partial-discussion-header");
  header.parentNode.insertBefore(fetchDiv, header.nextSibling);
}

// This function runs when the page loads.
window.onload = function() {
  // Parse the URL to get the user, repo, and PR number.
  var urlParts = window.location.pathname.split('/');
  var user = urlParts[1];
  var repo = urlParts[2];
  var prNum = urlParts[4];

  insertGitFetchCommand(user, repo, prNum);
};

