import com.auth0.jwt.JWT;
import com.auth0.jwt.algorithms.Algorithm;
import com.auth0.jwt.exceptions.JWTCreationException;
import com.auth0.jwt.exceptions.JWTVerificationException;
import com.auth0.jwt.interfaces.DecodedJWT;
import com.auth0.jwt.interfaces.JWTVerifier;

import java.security.interfaces.RSAPrivateKey;
import java.security.interfaces.RSAPublicKey;
import java.util.Date;

public class JwtUtils {
    private static final long EXPIRATION_TIME_MS = 15 * 60 * 1000; // 15 minutes

    private RSAPublicKey rsaPublicKey;
    private RSAPrivateKey rsaPrivateKey;

    public JwtUtils(RSAPublicKey rsaPublicKey, RSAPrivateKey rsaPrivateKey) {
        this.rsaPublicKey = rsaPublicKey;
        this.rsaPrivateKey = rsaPrivateKey;
    }

    public String createJwtToken(User user) {
        try {
            Algorithm algorithm = Algorithm.RSA256(rsaPublicKey, rsaPrivateKey);
            Date expiryDate = new Date(System.currentTimeMillis() + EXPIRATION_TIME_MS);

            String token = JWT.create()
                    .withIssuer("auth0")
                    .withClaim("userId", user.getId())
                    .withExpiresAt(expiryDate)
                    .sign(algorithm);
            return token;
        } catch (JWTCreationException exception) {
            // Handle exception if JWT creation fails
            exception.printStackTrace();
            return null;
        }
    }

    public boolean verifyJwtToken(String token) {
        try {
            Algorithm algorithm = Algorithm.RSA256(rsaPublicKey, rsaPrivateKey);
            JWTVerifier verifier = JWT.require(algorithm)
                    .withIssuer("auth0")
                    .build();
            DecodedJWT decodedJWT = verifier.verify(token);
            return true;
        } catch (JWTVerificationException exception) {
            // Handle exception if JWT verification fails
            exception.printStackTrace();
            return false;
        }
    }

    public String extractUserIdFromToken(String token) {
        try {
            DecodedJWT decodedJWT = JWT.decode(token);
            return decodedJWT.getClaim("userId").asString();
        } catch (JWTVerificationException exception) {
            // Handle exception if JWT decoding fails
            exception.printStackTrace();
            return null;
        }
    }
}

