cat << 'EOF' > src/execute/forwardOutput.js
function forwardOutput(stdout, stderr) {
  let output = "";
  if (stdout.length > 0) {
    console.log(stdout);
    output += stdout;
  }
  if (stderr.length > 0) {
    console.log(stderr);
    output += stderr;
  }
  return output;
}

export { forwardOutput };
EOF

# Step 2: Modify executeCode.js
cat << 'EOF' > src/execute/executeCode.js
#!/usr/bin/env node

import { startInteractiveSession } from "../interactiveSession/startInteractiveSession.js";
import { exec } from 'child_process';
import { writeFile } from 'fs';
import { forwardOutput } from './forwardOutput.js';

const executeCode = async (code, last_command_result, parent_message_id, rl) => {
  rl.question('\x1b[1mEXECUTE? [y/n]\x1b[0m ', async (answer) => {
    console.log("");
    if (answer.toLowerCase() === 'y' || answer === "") {
      writeFile('./change.sh', code, (err) => {
        if (err) {
          console.error(err.message);
          return;
        }
        exec(code, (error, stdout, stderr) => {
          if (error) {
            console.error(error.message);
            last_command_result = "Command failed. Output:\n" + error.message + "\n";
